import './App.css';
import pic from './assets/deneme2.jpeg'
import {useRef,useEffect,useState} from 'react'
import { socket } from './socket'

function App() {
   const ref = useRef();
  const [y,setY] = useState(0)
  const [x,setX] = useState(0)

   useEffect(() => {
    socket.emit('sayHello','Web Girdi');
    socket.on('data', (ds)=>{
      // console.log(ds)
      //id y
      if(ds.id == 'D43639AEA1AA' && ds.Mac =='123B6A1B4452'){
        setY(ds.distance)
      } 
      if(ds.id == '8030DCC74F9A' && ds.Mac =='123B6A1B4452'){
        setX(ds.distance)
      } 
    });

    socket.on('msg', (data) => {
      console.log(data)
  })

  },[])
   useEffect(() => {
    ref.current.style.top=`${y}px`
    ref.current.style.left=`${x}px`
   }, [y,x])

  return (
    <div className="App">
      <div className='conteiner'>
        <div className='point' ref={ref}>
          <div className='point-id'>
            <div>123B6A1B4452</div>
          </div>
        </div>


        <img className='bgpic' src={pic} alt={'denem'}></img>
      </div>
    </div>
  );
}

export default App;
