import './App.css';
import pic from './assets/deneme.png'
import {useRef,useEffect,useState} from 'react'
import { socket } from './socket'

function App() {
   const ref = useRef();
  const [y,setY] = useState(0)

   useEffect(() => {
    socket.emit('sayHello','Web Girdi');
    socket.on('data', (ds)=>{
      console.log(ds)
      //id y
      if(ds.id == 'D43639AEA1AA' && ds.Mac =='123B6A1B4452'){
        setY(ds.distance)
        ref.current.style.left=`${ds.distance}px`

      } 
      ref.current.style.left='200px'
    });

    socket.on('msg', (data) => {
      console.log(data)
  })

  },[ref])
   useEffect(() => {
    ref.current.style.top='20px'
    ref.current.style.left='200px'
   }, [])

  return (
    <div className="App">
      <div className='conteiner'>
        <div className='point' ref={ref}>
          <div className='point-id'>
            <div>asdasd</div>
          </div>
        </div>


        <img className='bgpic' src={pic} alt={'denem'}></img>
      </div>
    </div>
  );
}

export default App;
